suggestions = [
    "Chodzi ci o",
    "Masz na myśli",
    "Myślisz o"
    
]

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/ask', methods=['POST'])
def ask():
    data = request.get_json()
    pytanie = data.get('question', '').lower()

    # Szukaj dokładnych słów kluczowych
    odpowiedz = None
    for slowo_kluczowe, tresc_odpowiedzi in faq.items():
        if slowo_kluczowe in pytanie:
            odpowiedz = tresc_odpowiedzi
            break

    # Jeśli znaleziono odpowiedź, zwróć ją
    if odpowiedz:
        return jsonify({"answer": odpowiedz})

    # Jeśli brak dokładnej odpowiedzi, poszukaj najbardziej podobnego słowa kluczowego
    slowa_kluczowe = list(faq.keys())
    dopasowanie = get_close_matches(pytanie, slowa_kluczowe, n=1, cutoff=0.6)

    if dopasowanie:
        najblizsze_slowo = dopasowanie[0]
        losowa_sugestia = random.choice(suggestions)
        odpowiedz = f"{losowa_sugestia} '{najblizsze_slowo}'? {faq[najblizsze_slowo]}"
    else:
        odpowiedz = "Niestety, nie znam odpowiedzi na to pytanie. Spróbuj sformułować je inaczej."

    return jsonify({"answer": odpowiedz})

